  <q-page class="q-ma-md">
    <q-btn size="lg" color="primary" @click="benchmark">Benchmark</q-btn>
    {{ status }}
    <p>
      {{ text }}
      <br />
      {{ time_keygen }}
      <br />
      {{ time_enc }}
      <br />
      {{ time_dec }}
    </p>
    <q-btn size="lg" color="primary" @click="mult">Cipher multiplizieren</q-btn>
    <q-btn size="lg" color="primary" @click="xy">xy</q-btn>
  </q-page>
</template>

<script>
export default {
  name: "PageIndex",

  data: function() {
    return {
      config: {},
      status: "Ready",
      text: "0",
      time_keygen: 0,
      time_enc: 0,
      time_dec: 0
    };
  },
  methods: {
    xy() {},
    mult() {
      var bigInt = require("big-integer");
      var bits = 2048;
      var n = BigInt(
        ""
      );

      var phi = BigInt(
        ""
      );

      var plaintext_a = 123n;
      var plaintext_b = 5n;

      var cipher_a = this.paillierEncrypt(n, bits, plaintext_a);
      var cipher_b = this.paillierEncrypt(n, bits, plaintext_b);

      var cipher_mul = cipher_a * cipher_b;
      var cipher_mul = bigInt(cipher_mul).mod(bigInt(n).pow(2));

      var plaintext_mul = this.paillierDecrypt(n, bits, cipher_mul, phi);
      console.log(plaintext_a + " * " + plaintext_b + " = " + plaintext_mul);
    },
    async benchmark() {
      console.log("-----------------------");
      this.status = "Busy";
      var bits = 2048;
      this.text = 0;
      var t0 = performance.now();

      // var n = BigInt(
      //   ""
      // );

      // var phi = BigInt(
      //   ""
      // );

      // this.getRandomPaillier(n, bits);
      // -------------------------
      this.status = "Computing primes";
      console.log("Computing Primes");
      var t0_1 = performance.now();

      // Generate primes p and q
      const bigintCryptoUtils = require("bigint-crypto-utils");
      var p, q;
      await bigintCryptoUtils.prime(bits).then(value => {
        p = value;
      });
      await bigintCryptoUtils.prime(bits).then(value => {
        q = value;
      });

      var n = p * q;
      var phi = (p - 1n) * (q - 1n);
      var t1_1 = performance.now() - t0_1;
      console.log(t1_1 + "ms");
      this.time_keygen = performance.now() - t0_1;

      // -------------------------

      // Random Plaintext
      const random = require("random-bigint");
      var x = random(2);
      console.log("Plaintext: " + x);

      // encrypt plaintext
      var c = this.paillierEncrypt(n, bits, x);

      // decrypt ciphertext
      var c_de = this.paillierDecrypt(n, bits, c, phi);
      console.log("Decrypted Ciphertext: " + c_de);

      var t1 = performance.now() - t0;
      console.log(t1 + "ms");
      this.text = t1 + " ms";
      this.status = "Ready";
    },
    /**
     * Paillier Encrypt
     */
    paillierEncrypt(n, bits, x) {
      this.status = "Encrypting";
      console.log("Encrypting");
      var t0 = performance.now();
      var bigInt = require("big-integer");

      // Random r
      var r = 1n;
      r = this.getRandomPaillier(n, bits);

      // n**2
      var n_squared = bigInt(n).pow(2);

      // c1 = (1+n)**x mod n**2
      var c1 = bigInt(1n + n).modPow(x, n_squared);

      // c2 = r**n mod n**2
      var c2 = bigInt(r).modPow(n, n_squared);

      var c = bigInt(c1).multiply(c2);
      c = bigInt(c).mod(n_squared);

      c = BigInt(c.toString());

      var t1 = performance.now() - t0;
      console.log(t1 + "ms");
      this.time_enc = t1;
      return c;
    },
    /**
     * Paillier Decrypt
     */
    paillierDecrypt(n, bits, y, phi) {
      this.status = "Decrypting";
      console.log("Decrypting");
      var t0 = performance.now();
      var bigInt = require("big-integer");
      // n**2
      var n_squared = bigInt(n).pow(2);

      // x = (y**phi mod n**2) - 1
      var x = bigInt(y)
        .modPow(phi, n_squared)
        .subtract(1);
      // x = x /n
      x = bigInt(x).divide(n);
      // x = x * phi**-1
      x = bigInt(x).multiply(bigInt(phi).modInv(n));
      // x = x mod n
      x = bigInt(x).mod(n);

      x = BigInt(x.toString());

      var t1 = performance.now() - t0;
      this.time_dec = t1;
      console.log(t1 + "ms");
      return x;
    },
    /**
     * Get a random number for Paillier. I.e. in Zn*
     */
    getRandomPaillier(n, bits) {
      const random = require("random-bigint");
      var bigInt = require("big-integer");
      var ran = 0n;
      var gcd = 0;
      var counter = 0;
      do {
        ran = random(this.getRandomInt(0, bits));
        gcd = this.gcd(n, ran);
        counter++;
      } while (gcd != 1);
      return ran;
    },
    gcd(a, b) {
      var r = 0n;
      while (b != 0) {
        r = a % b;
        a = b;
        b = r;
      }
      return a;
    },
    /**
     * Random int between min and max
     */
    getRandomInt(min, max) {
      return (Math.random() * (max - min) + min) | 0;
    },
    /**
     * Inverse of a in Zn*
     *
     * Not used anymore
     */
    modInverse(a, m) {
      var m0 = m;
      var y = 0n,
        x = 1n;

      if (m == 1) return 0;

      while (a > 1) {
        // q is quotient
        var q = a / m;
        var t = m;

        // m is remainder now, process same as
        // Euclid's algo
        (m = a % m), (a = t);
        t = y;

        // Update y and x
        y = x - q * y;
        x = t;
      }

      // Make x positive
      if (x < 0) x += m0;

      return x;
    }
  }
};
</script>
